      return moodEntries.find(e => e.date === dateStr)?.rating || null;
    });

    // If the data point is actually null (missing data)
    if (rawData[index] === null) {
      return (
        <View key={index} style={{ position: 'absolute', left: x - 10, top: y - 10 }}>
          <Text style={{ color: theme.colors.error, fontSize: 16, fontWeight: 'bold' }}>X</Text>
        </View>
      );
    }
    return null;
  };

  const today = new Date();

  return (
    <SafeAreaView style={styles.safeArea}>
      <StatusBar barStyle="light-content" backgroundColor={theme.colors.background} />

      <View style={styles.header}>
        <TouchableOpacity 
          style={styles.backButton}
