<code filepath="components/MoodTrendGraph.tsx">
import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, Dimensions, ActivityIndicator } from 'react-native';
import { MoodEntry } from '../types';
import { theme } from '../theme/theme';
import { getRecentMoodEntries } from '../services/moodService';

// Get screen dimensions
const { width: screenWidth } = Dimensions.get('window');

interface MoodTrendGraphProps {
  days?: number;
}

export default function MoodTrendGraph({ 
  days = 5 
}: MoodTrendGraphProps) {
  const [moodEntries, setMoodEntries] = useState<MoodEntry[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadMoodEntries = async () => {
      setLoading(true);
      try {
        const entries = await getRecentMoodEntries(days);
        setMoodEntries(entries);
      } catch (error) {
        console.error('Error loading mood entries:', error);
      } finally {
        setLoading(false);
      }
    };

    loadMoodEntries();
  }, [days]);

  // Generate dates for the last 'days' days
  const generateDateRange = () => {
    const dateRange = [];
    const today = new Date();

    for (let i = days - 1; i >= 0; i--) {
      const date = new Date();
      date.setDate(today.getDate() - i);
      const dateStr = date.toISOString().split('T')[0];
      dateRange.push({
        date: dateStr,
        dayAbbr: getDayAbbreviation(dateStr),
        entry: moodEntries.find(entry => entry.date === dateStr)
      });
    }

    return dateRange;
  };

  // Get color for a specific mood rating
  const getMoodColor = (rating: number) => {
    switch(rating) {
      case 1: return theme.colors.mood1;
      case 2: return theme.colors.mood2;
      case 3: return theme.colors.mood3;
      case 4: return theme.colors.mood4;
      case 5: return theme.colors.mood5;
      default: return theme.colors.primary;
    }
  };

  // Get day abbreviation
  const getDayAbbreviation = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { weekday: 'short' }).slice(0, 1);
  };

  // Calculate if mood is improving based on actual data points
  const calculateMoodTrend = () => {
    const entriesWithData = moodEntries.filter(entry => entry.rating !== undefined);
    if (entriesWithData.length < 2) return null;

    const sortedEntries = [...entriesWithData].sort((a, b) => 
      new Date(a.date).getTime() - new Date(b.date).getTime()
    );

    const firstEntry = sortedEntries[0];
    const lastEntry = sortedEntries[sortedEntries.length - 1];

    if (lastEntry.rating > firstEntry.rating) return 'improving';
    if (lastEntry.rating < firstEntry.rating) return 'declining';
    return 'stable';
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="small" color={theme.colors.primary} />
        <Text style={styles.loadingText}>Loading mood data...</Text>
      </View>
    );
  }

  if (moodEntries.length === 0) {
    return (
      <View style={styles.emptyContainer}>
        <Text style={styles.emptyText}>No mood data available yet.</Text>
        <Text style={styles.emptySubtext}>Track your mood daily to see trends.</Text>
      </View>
    );
  }

  const dateRange = generateDateRange();
  const moodTrend = calculateMoodTrend();

  return (
    <View style={styles.container}>
      <View style={styles.graphContainer}>
        {dateRange.map((item, index) => (
          <View key={index} style={styles.dayColumn}>
            <View style={styles.barContainer}>
              {item.entry ? (
