// Get the current mood streak
export async function getMoodStreak(): Promise<number> {
  try {
    // Get the current user
    const { data: { session }, error: sessionError } = await supabase.auth.getSession();

    if (sessionError || !session) {
      console.log('No active session found when getting mood streak');
      // Return mock data if no session
      return getMockMoodStreak();
    }

    const userId = session.user.id;

    // Get all mood entries for this user, ordered by date descending
    const { data, error } = await supabase
      .from('mood_entries')
      .select('date')
      .eq('user_id', userId)
      .order('date', { ascending: false });

    if (error) {
      console.error('Error fetching mood entries for streak calculation:', error);
      // Return mock data on error
      return getMockMoodStreak();
    }

    if (!data || data.length === 0) {
      return 0;
    }

    // Calculate streak
    let streak = 0;

    // Get today's date in local time zone
    const today = getLocalToday();
    const todayDate = new Date(today);

    // Check if the most recent entry is from today
    const mostRecentDate = new Date(data[0].date);
    const mostRecentDateStr = mostRecentDate.toISOString().split('T')[0];

    // If the most recent entry is not from today or yesterday, streak is 0
    if (mostRecentDateStr !== today) {
      // Check if it's from yesterday
      const yesterday = new Date(todayDate);
      yesterday.setDate(yesterday.getDate() - 1);
      const yesterdayStr = yesterday.toISOString().split('T')[0];

      if (mostRecentDateStr !== yesterdayStr) {
        return 0; // Streak broken - most recent entry is older than yesterday
      }
    }

    // Create a map of dates with entries
    const dateMap = new Map();
    data.forEach(entry => {
      dateMap.set(entry.date, true);
    });

    // Start checking from today or the most recent entry date
    let currentDate = mostRecentDateStr === today ? todayDate : mostRecentDate;

    // Check consecutive days
    while (true) {
      const dateStr = currentDate.toISOString().split('T')[0];

      if (dateMap.has(dateStr)) {
        streak++;

        // Move to the previous day
        currentDate.setDate(currentDate.getDate() - 1);

        // If we've gone back more than 365 days, break to prevent infinite loop
        if (streak > 365) break;
      } else {
        break; // Streak ends when we find a day without an entry
      }
    }

    return streak;
  } catch (error) {
    console.error('Error in getMoodStreak:', error);
    // Return mock data on error
    return getMockMoodStreak();
  }
}

