<code filepath="screens/AdvancedMoodAnalyticsScreen.tsx">
import React, { useState, useEffect } from 'react';
import { 
  StyleSheet, 
  Text, 
  View, 
  ScrollView, 
  TouchableOpacity, 
  ActivityIndicator, 
  SafeAreaView, 
  StatusBar,
  Dimensions,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { theme } from '../theme/theme';
import { getRecentMoodEntries, MoodEntry } from '../services/moodService';
import { LineChart } from 'react-native-chart-kit';
import AsyncStorage from '@react-native-async-storage/async-storage';

// Get screen dimensions
const { width: screenWidth } = Dimensions.get('window');

interface AdvancedMoodAnalyticsScreenProps {
  navigation: any;
  route: any;
}

interface MoodPattern {
  day: string;
  pattern: string;
  description: string;
  hasData: boolean;
  averageMood?: number | null;
  entryCount?: number;
}

interface MoodTrigger {
  trigger: string;
  impact: 'positive' | 'negative';
  frequency: number;
}

interface DayAnalysis {
  day: string;
  entries: MoodEntry[];
  averageMood: number | null;
  entryCount: number;
  hasData: boolean;
}

export default function AdvancedMoodAnalyticsScreen({ navigation, route }: AdvancedMoodAnalyticsScreenProps) {
  const [isLoading, setIsLoading] = useState(true);
  const [moodEntries, setMoodEntries] = useState<MoodEntry[]>([]);
  const [hasEnoughData, setHasEnoughData] = useState(false);
  const [dayAnalytics, setDayAnalytics] = useState<DayAnalysis[]>([]);
  const [moodPatterns, setMoodPatterns] = useState<MoodPattern[]>([]);
  const [moodTriggers, setMoodTriggers] = useState<MoodTrigger[]>([]);
  const [isPremium, setIsPremium] = useState(route.params?.isPremium || false);
  const [userName, setUserName] = useState('');

  // Days of the week
  const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

  useEffect(() => {
    const loadMoodData = async () => {
      setIsLoading(true);
      try {
        // Get user name
        const storedName = await AsyncStorage.getItem('user_display_name');
        if (storedName) {
          setUserName(storedName);
        }

        // Get mood entries from the last 30 days
        const entries = await getRecentMoodEntries(30);
        setMoodEntries(entries);

        // Check if we have enough data for analysis (at least 10 entries)
        const hasEnough = entries.length >= 10;
        setHasEnoughData(hasEnough);

        if (hasEnough) {
          // Analyze mood patterns
          analyzeMoodPatterns(entries);
        }
      } catch (error) {
        console.error('Error loading mood data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadMoodData();
  }, [isPremium]);

  const analyzeMoodPatterns = (entries: MoodEntry[]) => {
    // Group entries by day of week
    const entriesByDay = daysOfWeek.map(day => {
      const dayEntries = entries.filter(entry => {
        const entryDate = new Date(entry.date);
        return daysOfWeek[entryDate.getDay()] === day;
      });

      return {
        day,
        entries: dayEntries,
        averageMood: dayEntries.length > 0 
          ? dayEntries.reduce((sum, entry) => sum + entry.rating, 0) / dayEntries.length 
          : null,
        entryCount: dayEntries.length,
        hasData: dayEntries.length > 0
      };
    });

    setDayAnalytics(entriesByDay);

    // Find patterns
    const patterns: MoodPattern[] = [];

    // Find the day with highest average mood
    const daysWithData = entriesByDay.filter(day => day.hasData);

    if (daysWithData.length > 0) {
      const highestMoodDay = [...daysWithData]
        .sort((a, b) => (b.averageMood || 0) - (a.averageMood || 0))[0];

      if (highestMoodDay && highestMoodDay.averageMood) {
        patterns.push({
          day: highestMoodDay.day,
          pattern: 'Peak Day',
          description: `You tend to feel your best on ${highestMoodDay.day}s with an average mood of ${highestMoodDay.averageMood.toFixed(1)}.`,
          hasData: true,
          averageMood: highestMoodDay.averageMood,
          entryCount: highestMoodDay.entryCount
        });
      }

      // Find the day with lowest average mood
      const lowestMoodDay = [...daysWithData]
        .sort((a, b) => (a.averageMood || 0) - (b.averageMood || 0))[0];

      if (lowestMoodDay && lowestMoodDay.averageMood) {
        patterns.push({
          day: lowestMoodDay.day,
          pattern: 'Dip Day',
          description: `You tend to experience lower moods on ${lowestMoodDay.day}s with an average mood of ${lowestMoodDay.averageMood.toFixed(1)}.`,
          hasData: true,
          averageMood: lowestMoodDay.averageMood,
          entryCount: lowestMoodDay.entryCount
        });
      }
    }

    // Check for weekend vs weekday pattern
    const weekdayEntries = entries.filter(entry => {
      const day = new Date(entry.date).getDay();
      return day >= 1 && day <= 5; // Monday to Friday
    });

    const weekendEntries = entries.filter(entry => {
      const day = new Date(entry.date).getDay();
      return day === 0 || day === 6; // Sunday or Saturday
    });

    // Only compare if we have data for both weekdays and weekends
    if (weekdayEntries.length > 0 && weekendEntries.length > 0) {
      const weekdayAvg = weekdayEntries.reduce((sum, entry) => sum + entry.rating, 0) / weekdayEntries.length;
      const weekendAvg = weekendEntries.reduce((sum, entry) => sum + entry.rating, 0) / weekendEntries.length;

      if (Math.abs(weekdayAvg - weekendAvg) > 0.5) {
        if (weekendAvg > weekdayAvg) {
          patterns.push({
            day: 'Weekend',
            pattern: 'Weekend Boost',
            description: `Your mood tends to improve on weekends by ${(weekendAvg - weekdayAvg).toFixed(1)} points on average.`,
            hasData: true
          });
        } else {
          patterns.push({
            day: 'Weekday',
            pattern: 'Weekday Preference',
            description: `You tend to have better moods during weekdays compared to weekends by ${(weekdayAvg - weekendAvg).toFixed(1)} points.`,
            hasData: true
          });
        }
      }
    }

    // Look for mood triggers in details
    const triggerWords = {
      positive: ['exercise', 'workout', 'friend', 'family', 'nature', 'outdoors', 'sleep', 'rest', 'meditation', 'hobby'],
      negative: ['work', 'stress', 'tired', 'sick', 'argument', 'conflict', 'deadline', 'anxiety', 'worry']
    };

    const triggers: Record<string, { count: number, totalImpact: number, type: 'positive' | 'negative' }> = {};

    // Analyze details for triggers
    entries.forEach(entry => {
      if (!entry.details) return;

      const details = entry.details.toLowerCase();

      // Check for positive triggers
      triggerWords.positive.forEach(word => {
        if (details.includes(word)) {
          if (!triggers[word]) {
            triggers[word] = { count: 0, totalImpact: 0, type: 'positive' };
          }
          triggers[word].count += 1;
          triggers[word].totalImpact += entry.rating;
        }
      });

      // Check for negative triggers
      triggerWords.negative.forEach(word => {
        if (details.includes(word)) {
          if (!triggers[word]) {
            triggers[word] = { count: 0, totalImpact: 0, type: 'negative' };
          }
          triggers[word].count += 1;
          triggers[word].totalImpact += entry.rating;
        }
      });
    });

    // Convert triggers to array and sort by frequency
    const triggersArray: MoodTrigger[] = Object.entries(triggers)
      .filter(([_, data]) => data.count >= 2) // Only include triggers that appear at least twice
      .map(([trigger, data]) => ({
        trigger: trigger.charAt(0).toUpperCase() + trigger.slice(1), // Capitalize first letter
        impact: data.type,
        frequency: data.count
      }))
      .sort((a, b) => b.frequency - a.frequency)
      .slice(0, 5); // Take top 5

    setMoodPatterns(patterns);
    setMoodTriggers(triggersArray);
  };

  const getMoodColor = (rating: number): string => {
    if (rating < 1.5) return theme.colors.mood1;
    if (rating < 2.5) return theme.colors.mood2;
    if (rating < 3.5) return theme.colors.mood3;
    if (rating < 4.5) return theme.colors.mood4;
    return theme.colors.mood5;
  };

  const getChartData = () => {
    const today = new Date();
    const labels: string[] = [];
    const data: (number | null)[] = [];
    const missingDataIndices: number[] = [];

    // Past 7 days data
    for (let i = 7; i >= 0; i--) {
      const date = new Date();
      date.setDate(today.getDate() - i);
      const dateStr = date.toISOString().split('T')[0];

      // Find entry for this date
      const entry = moodEntries.find(e => e.date === dateStr);

      if (i === 0) {
        labels.push('Today');
      } else {
        // Use shorter day abbreviations (Mo, Tu, We, etc.) to save space
        labels.push(daysOfWeek[date.getDay()].substring(0, 2));
      }

      // Use null for missing data instead of 0
      if (!entry) {
        missingDataIndices.push(7 - i); // Store the index of missing data
        data.push(null);
      } else {
        data.push(entry.rating);
      }
    }

    return {
      labels,
      datasets: [
        {
          data: data.map(d => d === null ? 0 : d), // Replace null with 0 for chart rendering
          color: (opacity = 1) => `rgba(134, 65, 244, ${opacity})`,
          strokeWidth: 2
        }
      ],
      legend: ["Mood Rating"],
      missingDataIndices // Add missing data indices to the chart data
    };
  };

  // Custom decorator for the chart to show red circles for missing data
  const renderDotContent = ({ x, y, index, indexData }: { x: number, y: number, index: number, indexData: number }) => {
    const chartData = getChartData();

    // If this index is in the missing data indices
    if (chartData.missingDataIndices.includes(index)) {
      return (
        <View 
          key={index} 
          style={{ 
            position: 'absolute', 
            left: x - 6, 
            top: y - 6,
            width: 12,
            height: 12,
            borderRadius: 6,
            backgroundColor: theme.colors.error,
          }} 
        />
      );
    }
    return null;
  };

  // Generate a description for a day based on its data
  const getDayDescription = (day: DayAnalysis): string => {
    if (!day.hasData) {
      return `No mood data available for ${day.day}s yet. Continue tracking to reveal patterns.`;
    }

    const avgMood = day.averageMood;
    if (avgMood === null) return `No mood data available for ${day.day}s yet.`;

    let description = `Your average mood on ${day.day}s is ${avgMood.toFixed(1)} out of 5`;

    // Add more context based on the average mood
    if (avgMood >= 4.5) {
      description += ` - these are your best days! Consider what makes ${day.day}s special.`;
    } else if (avgMood >= 3.5) {
      description += ` - you generally feel good on ${day.day}s.`;
    } else if (avgMood >= 2.5) {
      description += ` - your mood is typically neutral on ${day.day}s.`;
    } else if (avgMood >= 1.5) {
      description += ` - you tend to feel a bit low on ${day.day}s. Consider planning enjoyable activities.`;
    } else {
      description += ` - these appear to be challenging days for you. Consider extra self-care.`;
    }

    return description;
  };

  // Get pattern type for a day
  const getDayPatternType = (day: DayAnalysis): string => {
    if (!day.hasData) return 'Needs More Data';

    const avgMood = day.averageMood;
    if (avgMood === null) return 'Needs More Data';

    if (avgMood >= 4.5) return 'Excellent Day';
    if (avgMood >= 3.5) return 'Good Day';
    if (avgMood >= 2.5) return 'Average Day';
    if (avgMood >= 1.5) return 'Below Average';
    return 'Challenging Day';
  };

  return (
