  const [isPremium, setIsPremium] = useState(route.params?.isPremium || false);
  const [userName, setUserName] = useState('');

  // Days of the week
  const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

  useEffect(() => {
    const loadMoodData = async () => {
      setIsLoading(true);
      try {
        // Get user name
        const storedName = await AsyncStorage.getItem('user_display_name');
        if (storedName) {
          setUserName(storedName);
        }

        // Get mood entries from the last 30 days
        const entries = await getRecentMoodEntries(30);
        setMoodEntries(entries);

        // Check if we have enough data for analysis (at least 10 entries)
        const hasEnough = entries.length >= 10;
        setHasEnoughData(hasEnough);

        if (hasEnough) {
          // Analyze mood patterns
          analyzeMoodPatterns(entries);
        }
      } catch (error) {
        console.error('Error loading mood data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadMoodData();
  }, [isPremium]);

  const analyzeMoodPatterns = (entries: MoodEntry[]) => {
    // Group entries by day of week
    const entriesByDay = daysOfWeek.map(day => {
      const dayEntries = entries.filter(entry => {
        const entryDate = new Date(entry.date);
        return daysOfWeek[entryDate.getDay()] === day;
      });

      return {
        day,
        entries: dayEntries,
        averageMood: dayEntries.length > 0 
          ? dayEntries.reduce((sum, entry) => sum + entry.rating, 0) / dayEntries.length 
          : null
      };
    });

    // Find the day with highest average mood
    const highestMoodDay = [...entriesByDay]
      .filter(day => day.averageMood !== null)
      .sort((a, b) => (b.averageMood || 0) - (a.averageMood || 0))[0];

    // Find the day with lowest average mood
    const lowestMoodDay = [...entriesByDay]
      .filter(day => day.averageMood !== null)
      .sort((a, b) => (a.averageMood || 0) - (b.averageMood || 0))[0];

    // Create patterns for each day of the week
    const patterns: MoodPattern[] = daysOfWeek.map(day => {
      const dayData = entriesByDay.find(d => d.day === day);

      if (!dayData || dayData.entries.length === 0 || dayData.averageMood === null) {
        return {
          day,
          pattern: 'No Data',
          description: `Log your mood on ${day}s to get personalized insights.`,
          hasData: false,
          entryCount: 0
        };
      }

      // Generate unique insights for each day
      let pattern = '';
      let description = '';

      // Check if this is the highest or lowest mood day
      if (highestMoodDay && highestMoodDay.day === day) {
        pattern = 'Peak Day';
        description = `You tend to feel your best on ${day}s with an average mood of ${dayData.averageMood.toFixed(1)}.`;
      } else if (lowestMoodDay && lowestMoodDay.day === day) {
        pattern = 'Dip Day';
        description = `You tend to experience lower moods on ${day}s with an average mood of ${dayData.averageMood.toFixed(1)}.`;
      } else {
        // Generate unique insights based on the day's data
        const avgMood = dayData.averageMood;

        if (avgMood >= 4) {
          pattern = 'Positive Day';
          description = `${day}s are generally good days for you with an average mood of ${avgMood.toFixed(1)}.`;
        } else if (avgMood >= 3) {
          pattern = 'Balanced Day';
          description = `You typically maintain a balanced mood of ${avgMood.toFixed(1)} on ${day}s.`;
        } else {
          pattern = 'Challenging Day';
          description = `${day}s tend to be more challenging with an average mood of ${avgMood.toFixed(1)}.`;
        }

        // Add more specific insights based on the day
        if (day === 'Monday') {
          description += ' Consider starting your week with a positive routine.';
        } else if (day === 'Wednesday') {
          description += ' This mid-week point might be a good time to plan something enjoyable.';
        } else if (day === 'Friday') {
          description += ' The end of the work week shows this pattern for you.';
        } else if (day === 'Saturday' || day === 'Sunday') {
          description += ' Your weekend patterns are worth noting.';
        }
      }

      return {
        day,
        pattern,
        description,
        hasData: true,
        entryCount: dayData.entries.length
      };
