            <View style={styles.patternsContainer}>
              <Text style={styles.sectionTitle}>Your Mood Patterns</Text>

              {/* Display all days of the week first */}
              {daysOfWeek.map((day, index) => {
                const pattern = moodPatterns.find(p => p.day === day);

                // If no pattern exists for this day, create a default "No Data" pattern
                const displayPattern = pattern || {
                  day,
                  pattern: 'No Data',
                  description: `Log your mood on ${day}s to get personalized insights.`,
                  entryCount: 0,
