interface AdvancedMoodAnalyticsScreenProps {
  navigation: any;
  route: any;
}

interface MoodPattern {
  day: string;
  pattern: string;
  description: string;
  entryCount: number;
}

interface MoodTrigger {
  trigger: string;
  impact: 'positive' | 'negative';
  frequency: number;
